import { useState } from 'react'
import { postlarGetir, postSil, postGuncelle } from '../api'
import { useEffect } from 'react'
import { Card, CardActions, CardContent, CardMedia, Button, Typography, ButtonBase, TextField, Paper,Grid } from '@mui/material';
import { ThumbUp, Delete, MoreHoriz, ThumbDown } from "@mui/icons-material"
import FileBase from "react-file-base64";
import moment from "moment"
import axios from 'axios'
const Home = () => {

  const [currentId, setCurrentId] = useState(null)
  const user = JSON.parse(localStorage.getItem('profile'));
  const [data, setData] = useState([])
  const Veri = () => {

    try {
      postlarGetir().then((response) => {
        setData(response.data);
      })

    } catch (hata) {
      console.log(hata);
    }
  };
  useEffect(() => {
    Veri();


  }, [currentId, data._id, data]);



  const DeletePost = (id) => {
    try {
      postSil(id);

    } catch (error) {
      console.error('Silme işlemi sırasında bir hata oluştu:', error);

    }
  }
  const [postData, setPostData] = useState({

    title: "",
    message: "",
    tags: "",
    selectedFile: "",
  });


  const temizle = () => {
    setPostData({

      title: "",
      message: "",
      tags: "",
      selectedFile: "",
    });
    setCurrentId(null);
  };
  const HandleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!currentId) {
        const postDataWithUser = { ...postData, name: user?.result?.name };

        // Make sure to replace '/posts' with the correct endpoint URL
        await axios.post("http://localhost:3801/posts", postDataWithUser)
          .then((response) => {
            // Assuming setPostData is a function to update the state with the response data
            setPostData(response.data);
            console.log(postData)
          })
          .catch((error) => {
            // Handle error
            console.error('Error submitting post:', error);
          });
      }
      else {
        await postGuncelle(currentId, {...postData,name:user?.result?.name})
        setCurrentId(null);
      }
    }
catch (error) {
  console.error('Güncelleme işlemi sırasında bir hata oluştu:', error);
      }
      temizle();
    }



  if (!user?.result?.name) {
    return (
      <Paper sx={{ backgroundColor: '#e9ecef' }}>
        <Typography variant='h6' align='center'>
          Lütfen giriş yapınız
        </Typography>
      </Paper>
    )
  }
  return (


        <div className='container'   style={{justifyContent:"space-between",alignItems:"stretch"}}  >
          <div className='row' >

<div className='col-md-8'>

</div>





          </div>
          <div className='col-md-4' >
            <Paper sx={{ backgroundColor: "#e9ecef" }} >
              <form
                style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
                autoComplete="off"
                noValidate
                onSubmit={HandleSubmit}
              >
                <div style={{ width: "97%", margin: "10px auto" }}>
                  <FileBase
                    type="file"
                    multiple={false}
                    onDone={({ base64 }) =>
                      setPostData({ ...postData, selectedFile: base64 })
                    }
                  />
                </div>

                <Typography sx={{ marginBottom: "10px" }} variant="h6">
                  {currentId ? "POST GÜNCELLE" : "POST EKLE"}
                </Typography>


                <TextField
                  sx={{ margin: "5px" }}
                  name="title"
                  variant="outlined"
                  label="Oluşturan"
                  fullWidth
                  value={postData.title}
                  onChange={(e) => setPostData({ ...postData, title: e.target.value })}
                />
                <TextField
                  sx={{ margin: "5px" }}
                  name="message"
                  variant="outlined"
                  label="Oluşturan"
                  fullWidth
                  multiline
                  rows={4}
                  value={postData.message}
                  onChange={(e) =>
                    setPostData({ ...postData, message: e.target.value })
                  }
                />
                <TextField
                  sx={{ margin: "5px" }}
                  name="tags"
                  variant="outlined"
                  label="Oluşturan"
                  fullWidth
                  value={postData.tags}
                  onChange={(e) =>
                    setPostData({ ...postData, tags: e.target.value.split(",") })
                  }
                />

                <Button
                  sx={{ marginBottom: "10px" }}
                  variant="contained"
                  color="primary"
                  size="large"
                  type="submit"
                  fullWidth
                >
                  {currentId ? "GÜNCELLE" : "EKLE"}
                </Button>

                <Button
                  sx={{ marginBottom: "10px" }}
                  variant="contained"
                  color="secondary"
                  size="small"
                  onClick={temizle}
                  fullWidth

                >
                  Temizle
                </Button>
              </form>
            </Paper>
          </div>
        </div>


  )
}
export default Home
