import * as api from '../api/index'

export const GetPosts = () => async (dispatch) => {
  try {
    const { data } = await api.postlarGetir();
    const action = { type: 'FETCH_ALL', payload: data };
    dispatch(action);
  } catch (error) {
    console.log(error.message);
  }
};
export const LikePost = (id) => async (dispatch) => {

  try {

    const { data } = await api.begeniDegistir(id);

    dispatch({ type: 'LIKE', payload: data })

  } catch (error) {

  }
}

export const CreatePost = (post) => async (dispatch) => {

  try {
    const { data } = await api.postOlustur(post)
    dispatch({ type: 'CREATE', payload: data })
  } catch (error) {
    console.log(error.message);
  }
}
const UpdatePost = (id, post) => async (dispatch) => {
  try {
    const { data } = await api.postGuncelle(id, post);
    dispatch({ type: "UPDATE", payload: data })
  } catch (error) {
    console.log(error.message)
  }
}
const DeletePost = (id) => async (dispatch) => {

  try {

    await api.postSil(id);

    dispatch({ type: 'DELETE', payload: id })
  } catch (error) {
    console.log(error.message);
  }
}
export default { DeletePost, UpdatePost, CreatePost, GetPosts, LikePost }
