}
export const createPost = (post) => async (dispatch) => {
  try {
    dispatch({ type: 'POST_REQUEST' }); // Optional: Dispatch a loading action

    const { data } = await api.postOlustur(post);

    dispatch({ type: 'CREATE_POST_SUCCESS', payload: data }); // Dispatch success action
  } catch (error) {
    console.error('Error creating post:', error);

    dispatch({ type: 'CREATE_POST_FAILURE', payload: error.message }); // Dispatch failure action
  }
};
export const updatePost=(id,post)=>async (dispatch)=>{
  try {
    const {data}=await api.postGuncelle(id,post);
    dispatch({type:"UPDATE",payload:data})
  } catch (error) {
    console.log(error.message)
  }
}
export const deletePost=(id)=>async (dispatch)=>{

  try {

      await api.postSil(id);

      dispatch({type:'DELETE',payload:id})
  } catch (error) {
      console.log(error.message);
  }
}
export default {}
