import { useState } from 'react'
import { postlarGetir, postSil, postGuncelle } from '../api'
import { useEffect } from 'react'
import FileBase from "react-file-base64";
import moment from "moment"
import axios from 'axios'
const Home = () => {

  const [currentId, setCurrentId] = useState(null)
  const user = JSON.parse(localStorage.getItem('profile'));
  const [data, setData] = useState([])
  const Veri = () => {

    try {
      postlarGetir().then((response) => {
        setData(response.data);
      })

    } catch (hata) {
      console.log(hata);
    }
  };
  useEffect(() => {
    Veri();


  }, [currentId, data._id, data]);



  const DeletePost = (id) => {
    try {
      postSil(id);

    } catch (error) {
      console.error('Silme işlemi sırasında bir hata oluştu:', error);

    }
  }
  const [postData, setPostData] = useState({


    message: "",
    tags: "",
    selectedFile: "",
  });


  const temizle = () => {
    setPostData({


      message: "",
      tags: "",
      selectedFile: "",
    });
    setCurrentId(null);
  };
  const HandleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!currentId) {
        const postDataWithUser = { ...postData, name: user?.result?.name };

        // Make sure to replace '/posts' with the correct endpoint URL
        await axios.post("http://localhost:3801/posts", postDataWithUser)
          .then((response) => {
            // Assuming setPostData is a function to update the state with the response data
            setPostData(response.data);
            console.log(postData)
          })
          .catch((error) => {
            // Handle error
            console.error('Error submitting post:', error);
          });
      }
      else {
        await postGuncelle(currentId, { ...postData, name: user?.result?.name })
        setCurrentId(null);
      }
    }
    catch (error) {
      console.error('Güncelleme işlemi sırasında bir hata oluştu:', error);
    }
    temizle();
  }



  if (!user?.result?.name) {
    return (
      <div style={{ backgroundColor: '#e9ecef' }}>
        <h5 align='center'>
          Lütfen giriş yapınız
        </h5>
      </div>
    )
  }
  return (


    <div className='container pt-4' style={{ justifyContent: "space-between", alignItems: "stretch" }}  >
      <div className='row' >

        <div className='col-md-4 row'>

        </div>






        <div className='col-md-8 p-0 m-0' >
          <div className="p-3" style={{ backgroundColor: "white", borderRadius: "10px" }} >
            <h5 style={{ marginBottom: "10px", textAlign: "center" }} >
              {currentId ? "POST GÜNCELLE" : "POST EKLE"}
            </h5><br></br>

            <input type="text" className="form-control" placeholder="Gönderi başlat" data-toggle="modal" data-target="#exampleModal" style={{ height: "55px", borderRadius: "30px" }} />


   </div>
          <hr></hr>
          {data?.map((post) => {
            return (
              <>
            <div className='row'>


              <div className='col-1 '></div>
              <div className='col-md-10  p-0 pt-3' key={post._id}>
                <div className='card' style={{ display: "flex", flexDirection: "column", justifyContent: "space-between", borderRadius: "15px", height: "100%", position: "relative", backgroundColor: "#edede9" }}>

                  <div >
                    <h5 style={{ color: "black", fontSize: "15px" }}>{user.result.name}  </h5>
                    <h6 >{moment().fromNow()} </h6>
                  </div>
                  <div>

                    {(user?.result?.name === post?.name) && (
                      <button onClick={() => {
                        setCurrentId(post._id);

                        const selectedPost = data.find(item => item._id === post._id);

                        if (selectedPost) {
                          setPostData({


                            message: selectedPost.message,
                            tags: selectedPost.tags.join(','), // tags bir dizi olduğu için join kullanarak stringe çeviriyoruz
                            selectedFile: selectedPost.selectedFile
                          });
                        } else {
                          setPostData({ message: '', tags: '', selectedFile: '' });
                        }
                      }}>

                        <h6 style={{ position: "absolute", top: "10px", right: "20px", color: "gray" }} size="large" />
                      </button>
                    )}
                  </div>
                  <div>
                    <img src={post.selectedFile} alt='' style={{ width: "100%", height: "300px" }}></img>
                    <h6 style={{ display: "flex", justifyContent: "space-between", margin: "10px" }} variant='p' color="darkgray">{post.tags.map(tag => `#${tag} `)} </h6>

                    <p className='p'>{post.message} </p>
                    <p style={{ padding: "0 8px 8px", display: "flex", justifyContent: "space-beween" }}>

                      {/* <Button size="small" color="primary" disabled={!user?.result}>
                <BegeniKontrol />
                &nbsp;{post.likeCount}
              </Button> */}
                      {(user?.result?.name === post?.name) && (

                        <i className="fas fa-trash-alt" onClick={() => DeletePost(post._id)}></i>

                      )}
                    </p>

                  </div>
                </div>
              </div>
              <div className='col-1 '></div>
              </div>
              </>
            )
          })}
        </div>
      </div>
    </div>


  )
}
export default Home
