import { AppBar, Toolbar, Typography, Button, Avatar } from '@mui/material'
import { Link, useLocation, useNavigate } from 'react-router-dom'

import { useState, useEffect } from 'react'

import { jwtDecode } from 'jwt-decode';

export const Navbar = () => {
  const location = useLocation();

  const navigate = useNavigate();


  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));

  const Logout = () => {

    localStorage.clear()
    navigate('/signin');

    setUser(null);
  };

  useEffect(() => {

    setUser(JSON.parse(localStorage.getItem('profile')));

  }, [location]);
  useEffect(() => {

    const token = user?.token;

    if (token) {
      const decodedToken = jwtDecode(token);

      console.log(decodedToken);
      console.log(new Date().getTime());

      if (decodedToken.exp * 1000 < new Date().getTime()) Logout();
    }

    setUser(JSON.parse(localStorage.getItem('profile')));

  }, [location]);

  return (
    <>
      {user?.result ? (
        <div style={{ display: 'flex', justifyContent: 'space-around', width: '150px', alignItems: 'center', }}>
          <h5  >{user?.result.name.charAt(0)}</h5>
          <Button variant="contained" color="secondary" onClick={Logout}>Çıkış</Button>
        </div>
      ) : (
        <Button component={Link} to="/signin" variant="contained" color="secondary">GİRİŞ YAP</Button>
      )}

      <nav class="navbar navbar-expand-lg navbar-light bg-light p-0m-0">
        <a class="navbar-brand" href="#">Sosyal Medya</a>
        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>

        <div class="collapse navbar-collapse" id="navbarSupportedContent">
          <ul class="navbar-nav mr-auto">
            <li class="nav-item active">
              <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#">Link</a>
            </li>
            <li class="nav-item dropdown">
              <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Dropdown
              </a>
              <div class="dropdown-menu" aria-labelledby="navbarDropdown">
                <a class="dropdown-item" href="#">Action</a>
                <a class="dropdown-item" href="#">Another action</a>
                <div class="dropdown-divider"></div>
                <a class="dropdown-item" href="#">Something else here</a>
              </div>
            </li>
            <li class="nav-item">
              <a class="nav-link disabled" href="#">Disabled</a>
            </li>
          </ul>
          <form class="form-inline my-2 my-lg-0">
            <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
              <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
          </form>
        </div>
      </nav>
    </>


  )
}
