import axios from "axios"
const url="/posts"

const API=axios.create({baseURL:'http://localhost:2049'})

API.interceptors.request.use((req)=>{
    if(localStorage.getItem('profile')){
        req.headers.Authorization=`Bearer ${JSON.parse(localStorage.getItem('profile')).token}`
    }

    return req;
})
export const postlarGetir= ()=> axios.get(url)
export const createPost=(newPost)=>API.post(url,newPost);
export const postGuncelle=(id,updatedPost)=>API.patch(`${url}/${id}`,updatedPost)
export const postSil=async(id)=>await API.delete(`${url}/${id}`)
export const begeniDegistir=(id)=>API.patch(`${url}/${id}/likePost`)

export const girisYap=(formData)=>axios.post('/user/signin',formData)


export const uyeOl = async (formData) => {
  try {
    const response = await axios.post('/user/signup', formData);
    // Assuming the server response contains useful information, you can access it here
    const responseData = response.data;

    // Handle the response or perform additional actions if needed

    return responseData; // You can return the response data if necessary
  } catch (error) {
    // Handle errors here
    console.error('Signup request failed:', error);

    // Optionally, rethrow the error to allow the caller to handle it
    throw error;
  }
};
