import axios from "axios";

const baseURL = 'http://localhost:2049';
const API = axios.create({ baseURL });

API.interceptors.request.use((req) => {
    if (localStorage.getItem('profile')) {
        req.headers.Authorization = `Bearer ${JSON.parse(localStorage.getItem('profile')).token}`;
    }
    return req;
});

export const postlarGetir = () => API.get("/posts");
export const postOlustur = (newPost) => API.post("/posts", newPost);
export const postGuncelle = (id, updatedPost) => API.patch(`/posts/${id}`, updatedPost);
export const postSil = (id) => API.delete(`/posts/${id}`);
export const begeniDegistir = (id) => API.patch(`/posts/${id}/likePost`);
export const girisYap = (formData) => API.post('/user/signin', formData);
export const uyeOl = (formData) => API.post('/user/signup', formData);

export default API; // Export the API instance for potential reuse in other parts of your application
